import { createSlice } from '@reduxjs/toolkit';

export const cartSlice = createSlice({
  name: 'Cart',
  initialState: [],
  reducers: {
    addToCart: (state, { payload }) => {
      const exist = state.find(item => item._id === payload._id);
      if (exist) {
        return state.map(item =>
          item._id === payload._id ? { ...payload, qty: exist.qty + 1 } : item
        );
      } else {
        state.push({ ...payload, qty: 1 });
      }
    },
    removeFromCard: (state, { payload }) => {
      const { qty } = state.find(item => item._id === payload._id);
      if (qty === 0) {
        return state.filter(item => item._id !== payload._id);
      } else {
        return state.map(item =>
          item._id === payload._id ? { ...payload, qty: item.qty - 1 } : item
        );
      }
    },
    writeState: (state, [payload]) => [(state = payload)],
  },
});

export const { addToCart, removeFromCard, writeState } = cartSlice.actions;
