import { useSelector, useDispatch } from 'react-redux';
import {
  addToCart,
  removeFromCard,
  writeState,
} from 'redux/products/productsSlice';
import { Ul, Li } from './CartProducts.styled';

const CartProducts = () => {
  const products = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  const onInput = (e, _id) => {
    const value = e.target.value;
    console.log(value);
    dispatch(writeState(e.target.value));
  };
  return (
    <Ul>
      {products.map(({ _id, price, name, shop, qty, image }) => {
        return (
          <Li key={_id}>
            <p>{name}</p>

            <p>{price}</p>
            <img src={image} alt={name} width="150px" />
            <input
              type="number"
              step={1}
              value={qty}
              onChange={() => onInput(_id)}
            />
            <button
              onClick={() =>
                dispatch(addToCart({ _id, name, price, shop, image }))
              }
            >
              +
            </button>
            <button
              onClick={() =>
                dispatch(removeFromCard({ _id, name, price, shop, image }))
              }
            >
              {' '}
              -
            </button>
          </Li>
        );
      })}
    </Ul>
  );
};

export default CartProducts;
