import styled from '@emotion/styled';

export const Ul = styled.ul`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  margin-left: 50px;
  margin-right: 30px;
`;

export const Li = styled.li`
  margin: 30px;
  width: 150px;
  border: black solid 1px;
  padding: 20px;
  border-radius: 15px;
`;
