import axios from 'axios';
const url = 'http://localhost:3001';

// const API = axios.create({ baseURL: 'http://localhost:3001' });

const FetchProducts = () => axios.get(`${url}/api/products/`);
const getOneProduct = _id => axios.get(`${url}/api/products/${_id}`);
const makeOrder = ({ name, email, phone, address, order, total }) =>
  axios.post(`${url}/api/products/`, {
    name,
    email,
    phone,
    address,
    order,
    total,
  });

const get = {
  FetchProducts,
  getOneProduct,
  makeOrder,
};

export default get;
