import { addToCart, removeFromCard } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';
// import get from '../components/API/Api';
import { useDispatch } from 'react-redux';
import { useState } from 'react';

const CartView = () => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');

  const products = useSelector(state => state.Cart);
  const dispatch = useDispatch();
  const onChange = e => {
    switch (name) {
      case 'name':
        setName(e.target.value);
        break;
      case 'phone':
        setPhone(e.target.value);
        break;
      case 'email':
        setEmail(e.target.value);
        break;
      case 'address':
        setAddress(e.target.value);
        break;
      default:
        console.log('no meaning');
    }
  };
  console.log(name);

  const result = products.map(item => Number(item.price) * Number(item.qty));
  const total = result.reduce((sum, elm) => sum + elm, 0);
  const onOrder = e => {
    e.preventDefault();
    console.log(e.target.value);
  };
  return (
    <>
      {products.length === 0 ? (
        <p>your cart is empty</p>
      ) : (
        <>
          <div>
            <h3>leave your contact</h3>
            <form onSubmit={onOrder}>
              <label>
                {' '}
                Name
                <input name="name" value={name} onChange={onChange} />
                Email
                <input name="email" value={email} onChange={onChange} />
              </label>
              <label>
                {' '}
                Phone
                <input value={phone} />
              </label>
              <label>
                {' '}
                Address
                <input value={address} />
              </label>
              <button type="submit">Order</button>
            </form>
          </div>
          {products.map(({ _id, price, name, shop, qty, image }) => {
            return (
              <li key={_id}>
                <p>{name}</p>
                <p>{qty}</p>
                <p>{price}</p>
                <img src={image} alt={name} width="150px" />
                <button
                  onClick={() =>
                    dispatch(addToCart({ _id, name, price, shop, image }))
                  }
                >
                  ^
                </button>
                <button
                  onClick={() =>
                    dispatch(removeFromCard({ _id, name, price, shop, image }))
                  }
                >
                  {' '}
                  less
                </button>
              </li>
            );
          })}
          <p>{total}</p>
        </>
      )}
    </>
  );
};

export default CartView;
