import { createSlice } from '@reduxjs/toolkit';

export const cartSlice = createSlice({
  name: 'cart',
  initialState: [],
  reducers: {
    addToCart: (state, { payload }) => {
      console.log(state);
      const exist = state.find(item => item.id === payload);
      if (exist) {
        console.log(exist);
        // state[indexOf(exist)]: qty = qty+1
        // state.map(item =>
        //   item.id === payload ? { ...exist, qty: exist.qty + 1 } : item
        // );
      } else {
        state.push({ id: payload, qty: 1 });
      }
    },
  },
});

export const { addToCart } = cartSlice.actions;
