import { useEffect, useState } from 'react';
import { addToCart } from 'redux/products/productsSlice';
import { useDispatch, useSelector } from 'react-redux';
import Products from '../components/Products/Products';
// import { useGetProductsQuery } from 'redux/products/productsSlice';

import get from '../API/Api';

const ShopView = () => {
  //   const { data } = useGetProductsQuery();
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState('');
  //   const [cart, setCart] = useState([]);

  useEffect(() => {
    get
      .FetchProducts()
      .then(data => {
        setProducts(data.data.data.result);
      })
      .catch(error => console.log(error.message));
  }, []);

  console.log(products);

  const cart = useSelector(state => state.art);
  const dispatch = useDispatch();

  const uArray = array => {
    var out = [];
    for (var i = 0, len = array.length; i < len; i++)
      if (out.indexOf(array[i]) === -1) out.push(array[i]);
    return out;
  };

  const allShops = products?.map(item => item.shop);
  const shops = uArray(allShops);

  const filteredProducts = products?.filter(item => item.shop === filter);
  return (
    <div>
      <div>
        <p onClick={() => setFilter('')}>All shops</p>
        {shops?.map(item => {
          return (
            <p key={item.index} onClick={() => setFilter(item)}>
              {' '}
              {item}
            </p>
          );
        })}
      </div>
      <div>
        {filter ? (
          <Products products={filteredProducts} />
        ) : (
          <Products products={products} />
        )}

        {/* <Products products={filter ? filteredProducts : products} /> */}
      </div>
    </div>
  );
};

export default ShopView;
