import styled from '@emotion/styled';

export const Div = styled.div`
  display: flex;
  flex-direction: column;
  border: black solid 1px;
  border-radius: 15px;
  padding: 30px 40px;
  margin: 0 30px;
`;
export const Label = styled.label`
  display: flex;
  flex-direction: column;
  margin: 10px;
`;
export const Input = styled.input``;
