import { addToCart } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';

import get from '../components/API/Api';
import { useEffect, useMemo } from 'react';

const CartView = () => {
  const products = useSelector(state => state.Cart);

  const getAll = id => {
    const data = get
      .getOneProduct(id)
      .then(data => data)
      .catch(error => console.log(error.message));
    const result = data.data.result;
    return result;
  };

  const allProductInTheCard = products.map(item => {
    return {
      result: getAll(item.id),
      qty: item.qty,
    };
  });
  console.log(allProductInTheCard);

  return (
    <>
      <p>hi rh==there</p>
      {allProductInTheCard.map(item => {
        return (
          <li>
            {/* <p>{item.result.name}</p> */}
            <p>{item.qty}</p>
          </li>
        );
      })}
      <button onClick={() => console.log(allProductInTheCard)}>
        gdvfjyhdf
      </button>
    </>
  );
};

export default CartView;
