import styled from '@emotion/styled';

export const Ul = styled.ul`
  border: black solid 1px;
  border-radius: 15px;
  display: flex;
  flex-direction: column;
  //   flex-wrap: wrap;
  margin-left: 50px;
  margin-right: 30px;
  margin-top: 0px;
  max-height: 700px;
  overflow-y: scroll;
  &::-webkit-scrollbar {
    width: 10px;
    border-top-right-radius: 20px;
    border-bottom-right-radius: 20px;
    background-color: #d0a686;
  }
  &::-webkit-scrollbar-thumb {
    width: 10px;
    background-color: #7b7169;
    border-radius: 20px;
  }
`;

export const Li = styled.li`
  margin: 20px;
  width: 700px;
  max-height: 150px;
  border: black solid 1px;
  padding: 20px;
  border-radius: 15px;
  list-style-type: none;
  display: flex;
`;

export const Div = styled.div`
  display: block;
  margin-left: 20px;
  text-align: center;
  padding: 0 40px;
`;

export const Qty = styled.div`
  text-align: center;
  width: 350px;
  height: 40px;
  border: black solid 1px;
`;
