import { addToCart } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';

import get from '../components/API/Api';

const CartView = () => {
  const products = useSelector(state => state.Cart);

  const allProductInTheCard = [];
  const cart = products.forEach(({ id, qty }) => {
    get
      .getOneProduct(id)
      .then(data => {
        const result = data.data.data.result;
        console.log(result);
        const heg = { qty, result };
        allProductInTheCard(heg);
        allProductInTheCard.push(heg);
      })
      .catch(error => console.log(error));
  });
  console.log(allProductInTheCard);
  return (
    <>
      {cart?.map(item => {
        return (
          <li>
            <p>{item.qty}</p>
            <p>{item.result.name}</p>
          </li>
        );
      })}
    </>
  );
};

export default CartView;
