import { useEffect, useState } from 'react';
import { useGetProductsQuery } from 'redux/products/productsSlice';

import get from '../components/API/Api';

const ShopView = () => {
  //   const { data } = useGetProductsQuery();
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState('');
  const [cart, setCart] = useState([]);

  const allProductInTheCard = [];

  useEffect(() => {
    get
      .FetchProducts()
      .then(data => {
        console.log(data.data.data.result);
        setProducts(data.data.data.result);
      })
      .catch(error => console.log(error.message));
  }, []);

  const onAdd = product => {
    const exist = cart.find(item => item.id === product);
    if (exist) {
      setCart(
        cart.map(item =>
          item.id === product ? { ...exist, qty: exist.qty + 1 } : item
        )
      );
    } else {
      setCart([...cart, { id: product, qty: 1 }]);
    }
  };

  const getCart = () => {
    cart.forEach(({ id, qty }) => {
      get
        .getOneProduct(id)
        .then(data => {
          const result = data.data.data.result;
          //  = result;
          const heg = { qty, result };
          allProductInTheCard.push(heg);
        })
        .catch(error => console.log(error));
    });
    console.log(allProductInTheCard);
  };

  const uArray = array => {
    var out = [];
    for (var i = 0, len = array.length; i < len; i++)
      if (out.indexOf(array[i]) === -1) out.push(array[i]);
    return out;
  };
  //   const products = data?.data.result;
  console.log(products);
  const allShops = products?.map(item => item.shop);
  const shops = uArray(allShops);

  const filteredProducts = products?.filter(item => item.shop === filter);

  return (
    <div>
      <p onClick={() => setFilter('')}>All shops</p>
      {shops?.map(item => {
        return <p onClick={() => setFilter(item)}> {item}</p>;
      })}
      {filter
        ? filteredProducts.map(({ _id, name, price, shop }) => {
            return (
              <li key={_id}>
                <h3>{name}</h3>
                <p>{price}</p>
                <p> {shop}</p>
                <button onClick={() => setCart(() => onAdd(_id))}>
                  Add to Card
                </button>
              </li>
            );
          })
        : products?.map(({ _id, name, price, shop }) => {
            return (
              <li key={_id}>
                <h3>{name}</h3>
                <p>{price}</p>
                <p> {shop}</p>
                <button onClick={() => onAdd(_id)}>Add to Card</button>
              </li>
            );
          })}
      <button onClick={() => getCart()}>hhhh</button>
    </div>
  );
};

export default ShopView;
