import { useDispatch } from 'react-redux';
import { addToCart } from 'redux/products/productsSlice';

const Products = (products = []) => {
  const dispatch = useDispatch();
  return products?.map(({ _id, name, price, shop, image }) => {
    return (
      <li key={_id}>
        <h3>{name}</h3>
        <p>{price}</p>
        <p> {shop}</p>
        <img src={image} alt={name} width="150px" />
        <button
          onClick={() => dispatch(addToCart({ _id, name, price, shop, image }))}
        >
          Add to Card
        </button>
      </li>
    );
  });
};

export default Products;
