import styled from '@emotion/styled';

export const Ul = styled.ul`
  border: black solid 1px;
  border-radius: 15px;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  margin-left: 50px;
  margin-right: 30px;
  height: 750px;
  overflow-y: scroll;
`;

export const Li = styled.li`
  margin: 30px;
  width: 150px;
  border: black solid 1px;
  padding: 20px;
  border-radius: 15px;
  list-style-type: none;
`;

export const Button = styled.button`
  margin: 15px 5px 0px 45px;
  border-radius: 5px;
  color: white;
  padding: 5px 10px;
  background-color: #d0a686;
  border: black solid 1px;
`;
