import { addToCart } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';

import get from '../components/API/Api';
import { useEffect, useState } from 'react';

const CartView = () => {
  const products = useSelector(state => state.Cart);
  const [allCart, setAllCart] = useState([]);
  const [didMount, setDidMount] = useState(false);

  useEffect(() => {
    setDidMount(true);
  }, []);

  useEffect(() => {
    if (didMount) {
      products.forEach(item => {
        get
          .getOneProduct(item.id)
          .then(data => {
            const { name, _id, shop, price, detail, image } =
              data.data.result[0];
            // const name = result.name;
            console.log(name);
            setAllCart(prevState => [
              ...prevState,
              {
                name,
                _id,
                shop,
                price,
                detail,
                image,
                qty: item.qty,
              },
            ]);
          })
          .catch(error => console.log(error.message));
      });
    }
  }, [products]);

  console.log(allCart);

  return (
    <>
      <p>hi rh==there</p>
      {allCart.map(item => {
        return (
          <li>
            <p>{item.name}</p>
            <p>{item.qty}</p>
          </li>
        );
      })}
      {/* <button onClick={() => console.log(allProductInTheCard)}>
        gdvfjyhdf
      </button> */}
    </>
  );
};

export default CartView;
