import { addToCart } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';

import get from '../components/API/Api';
import { useEffect } from 'react';

const CartView = () => {
  const products = useSelector(state => state.Cart);

  const allProductInTheCard = [];
  useEffect(
    () =>
      products.forEach(({ id, qty }) => {
        get
          .getOneProduct(id)
          .then(data => {
            const result = data.data.data.result;

            const heg = { qty, result };
            allProductInTheCard.push(heg);
          })
          .catch(error => console.log(error));
      }),
    []
  );

  return (
    <>
      <p>hi rh==there</p>
      {allProductInTheCard.map(item => {
        return (
          <li>
            <p>{item.qty}</p>
          </li>
        );
      })}
      <button onClick={() => console.log(allProductInTheCard)}>
        gdvfjyhdf
      </button>
    </>
  );
};

export default CartView;
