import { addToCart } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';
import get from '../components/API/Api';
import { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';

const CartView = () => {
  const products = useSelector(state => state.Cart);
  const [allCart, setAllCart] = useState([]);
  const dispatch = useDispatch();
  // const [didMount, setDidMount] = useState(false);

  // useEffect(() => {
  //   setDidMount(true);
  // }, []);

  // useEffect(() => {
  const filtered = products.forEach(item => {
    get
      .getOneProduct(item.id)
      .then(data => {
        const { name, _id, shop, price, detail, image } = data.data.result[0];
        // const name = result.name;
        console.log(name);
        setAllCart(prevState => [
          ...prevState,
          {
            name,
            _id,
            shop,
            price,
            detail,
            image,
            qty: item.qty,
          },
        ]);
      })
      .catch(error => console.log(error.message));
  });
  // }, [products]);

  // const ids = allCart.map(o => o._id);
  // const filtered = allCart.filter(
  //   ({ _id }, index) => !ids.includes(_id, index + 1)
  // );
  console.log(filtered);

  return (
    <>
      <p>hi rh==there</p>
      {allCart.map(item => {
        return (
          <li>
            <p>{item.name}</p>
            <p>{item.qty}</p>
            <button onClick={() => dispatch(addToCart(item._id))}>
              Add to Card
            </button>
          </li>
        );
      })}
      {/* <button onClick={() => console.log(allProductInTheCard)}>
        gdvfjyhdf
      </button> */}
    </>
  );
};

export default CartView;
