import { useEffect, useState } from 'react';

import get from '../components/API/Api';

const ShopView = () => {
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState('');
  const [cart, setCart] = useState([]);
  const convertArrayToObject = (array, key) => {
    const initialValue = {};
    return array.reduce((obj, item) => {
      return {
        ...obj,
        [item[key]]: item,
      };
    }, initialValue);
  };
  const allProductInTheCard = [];

  const getCart = () => {
    cart.forEach(item => {
      get
        .getOneProduct(item)
        .then(data => allProductInTheCard.push(data.data.data.result))
        .catch(error => console.log(error));
    });
  };
  console.log(allProductInTheCard);

  useEffect(() => {
    get
      .FetchProducts()
      .then(data => {
        setProducts(data.data.data.result);
      })
      .catch(error => console.log(error));
  }, []);

  const uArray = array => {
    var out = [];
    for (var i = 0, len = array.length; i < len; i++)
      if (out.indexOf(array[i]) === -1) out.push(array[i]);
    return out;
  };
  const allShops = products.map(item => item.shop);
  const shops = uArray(allShops);

  const filteredProducts = products?.filter(item => item.shop === filter);

  return (
    <div>
      <p onClick={() => setFilter('')}>All shops</p>
      {shops.map(item => {
        return <p onClick={() => setFilter(item)}> {item}</p>;
      })}
      {filter
        ? filteredProducts.map(({ _id, name, price, shop }) => {
            return (
              <li key={_id}>
                <h3>{name}</h3>
                <p>{price}</p>
                <p> {shop}</p>
                <button
                  onClick={() => setCart(prevState => [...prevState, _id])}
                >
                  Add to Card
                </button>
              </li>
            );
          })
        : products.map(({ _id, name, price, shop }) => {
            return (
              <li key={_id}>
                <h3>{name}</h3>
                <p>{price}</p>
                <p> {shop}</p>
                <button
                  onClick={() => setCart(prevState => [...prevState, _id])}
                >
                  Add to Card
                </button>
              </li>
            );
          })}
      <button onClick={() => getCart()}>hhhh</button>
    </div>
  );
};

export default ShopView;
