import { addToCart, removeFromCard } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';
// import get from '../components/API/Api';
import { useDispatch } from 'react-redux';

const CartView = () => {
  const products = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  const result = products.map(item => Number(item.price) * Number(item.qty));
  const total = result.reduce((sum, elm) => sum + elm, 0);
  console.log(total);
  return (
    <>
      <p>hi rh==there</p>
      {products.length === 0 ? (
        <p>your cart is empty</p>
      ) : (
        <>
          <div>
            <h3>leave your contact</h3>
            <form>
              <label>
                {' '}
                Name
                <input />
              </label>
              <label>
                {' '}
                Email
                <input />
              </label>
              <label>
                {' '}
                Phone
                <input />
              </label>
              <label>
                {' '}
                Address
                <input />
              </label>
              <button type="submit">Order</button>
            </form>
          </div>
          {products.map(({ _id, price, name, qty }) => {
            return (
              <li>
                <p>{name}</p>
                <p>{qty}</p>
                <p>{price}</p>
                <button
                  onClick={() => dispatch(addToCart({ _id, name, price }))}
                >
                  Add to Card
                </button>
                <button
                  onClick={() => dispatch(removeFromCard({ _id, name, price }))}
                >
                  {' '}
                  less
                </button>
              </li>
            );
          })}
          <p>{total}</p>
        </>
      )}
    </>
  );
};

export default CartView;
