import { addToCart, removeFromCard } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';
// import get from '../components/API/Api';
import { useDispatch } from 'react-redux';
import { useState } from 'react';
import get from '../API/Api';

const CartView = () => {
  // const [name, setName] = useState('');
  // const [phone, setPhone] = useState('');
  // const [email, setEmail] = useState('');
  // const [address, setAddress] = useState('');
  const [state, setState] = useState({
    name: '',
    phone: '',
    email: '',
    address: '',
  });

  const products = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  const onChange = e => {
    setState(prevState => {
      return { ...prevState, [e.target.name]: e.target.value };
    });
  };

  const result = products.map(item => Number(item.price) * Number(item.qty));
  const total = result.reduce((sum, elm) => sum + elm, 0);
  const onOrder = e => {
    e.preventDefault();
    const { name, email, phone, address } = state;
    const order = {
      name: name,
      email: email,
      phone: phone,
      address: address,
      products: products,
      total: total,
    };
    get.makeOrder(order);

    console.log(state);
  };
  return (
    <>
      {products.length === 0 ? (
        <p>your cart is empty</p>
      ) : (
        <>
          <div>
            <h3>leave your contact</h3>
            <form onSubmit={onOrder}>
              <label>
                {' '}
                Name
                <input name="name" value={state.name} onChange={onChange} />
              </label>
              <label>
                {' '}
                Email
                <input name="email" value={state.email} onChange={onChange} />
              </label>
              <label>
                {' '}
                Phone
                <input name="phone" value={state.phone} onChange={onChange} />
              </label>
              <label>
                {' '}
                Address
                <input
                  name="address"
                  value={state.address}
                  onChange={onChange}
                />
              </label>
              <button type="submit">Order</button>
            </form>
          </div>
          {products.map(({ _id, price, name, shop, qty, image }) => {
            return (
              <li key={_id}>
                <p>{name}</p>
                <p>{qty}</p>
                <p>{price}</p>
                <img src={image} alt={name} width="150px" />
                <button
                  onClick={() =>
                    dispatch(addToCart({ _id, name, price, shop, image }))
                  }
                >
                  ^
                </button>
                <button
                  onClick={() =>
                    dispatch(removeFromCard({ _id, name, price, shop, image }))
                  }
                >
                  {' '}
                  less
                </button>
              </li>
            );
          })}
          <p>{total}</p>
        </>
      )}
    </>
  );
};

export default CartView;
