import { addToCart } from 'redux/products/productsSlice';
import { useSelector } from 'react-redux';

import get from '../components/API/Api';
import { useEffect, useMemo, useState } from 'react';

const CartView = () => {
  const products = useSelector(state => state.Cart);
  const [allCart, setAllCart] = useState([]);

  const getAll = id => {
    get
      .getOneProduct(id)
      .then(data => data)
      .catch(error => console.log(error.message));
    // const result = data.data.data.result;
    // console.log(result);
    // return result;
  };
  useEffect(
    () =>
      products.forEach(item => {
        get
          .getOneProduct(item.id)
          .then(data => {
            console.log(data);
            setAllCart(prevState => [
              ...prevState,
              {
                ...data.data.data,
                qty: item.qty,
              },
            ]);
          })
          .catch(error => console.log(error.message));
      }),
    [products]
  );

  console.log(allCart);

  return (
    <>
      <p>hi rh==there</p>
      {allCart.map(item => {
        return (
          <li>
            {/* <p>{item.result.name}</p> */}
            <p>{item.qty}</p>
          </li>
        );
      })}
      {/* <button onClick={() => console.log(allProductInTheCard)}>
        gdvfjyhdf
      </button> */}
    </>
  );
};

export default CartView;
